import React from "react";
import { styled } from "styled-components";
import { ReactComponent as HeaderImg } from "../images/mypage.svg";
import { ReactComponent as ProfileImg } from "../images/profile.svg";
import { ReactComponent as NextImg } from "../images/nextbtn.svg";
import { ReactComponent as PrevImg } from "../images/prevbtn.svg";
import Footer from "../components/Footer";

export default function MatchingProfile() {
  const userData = [
    {
      name: "AlmondBriize",
      sex: "여성",
      age: 23,
      location: "합정역 2호선",
    },
  ];

  const postList = [
    {
      title: "크리스마스 쿠키 클래스 같이 들어요!",
      state: true,
    },
    {
      title: "뉴진스 팬미팅 동행 구합니다.",
      state: false,
    },
    {
      title: "전시회 같이 갈 사람",
      state: false,
    },
    {
      title: "배구경기 같이 보러갈 분 구합니다.",
      state: false,
    },
    {
      title: "라이즈 팝업 같이 가요!",
      state: false,
    },
  ];

  const reviewList = [
    {
      detail: "함께 해서 즐거웠습니다!",
    },
    {
      detail: "다음에 기회가 된다면 또 뵈었으면 좋겠네요!",
    },
  ];
  return (
    <div>
      <HeaderImg />
      <ProfileBox>
        <ProfileImg />
        <UserName>AlmondBriize(여성/23)</UserName>
        <UserLocation>합정역 2호선</UserLocation>
      </ProfileBox>
      <ContentsBox>
        <PostBox>
          <h2>작성한 글</h2>
          <PrevImg />
          <NextImg />
        </PostBox>
        <ReviewBox>
          <div className="">작성된 후기</div>
          <p className="">{reviewList[0].detail}</p>
          <p className="">{reviewList[1].detail}</p>
        </ReviewBox>
      </ContentsBox>
      <Footer />
    </div>
  );
}

const ProfileBox = styled.div`
  text-align: center;
  margin-top: 72px;
`;

const UserName = styled.div`
  color: var(--Black, #222);
  font-family: Pretendard;
  font-size: 36px;
  font-style: normal;
  font-weight: 600;
  line-height: 140%; /* 50.4px */
  letter-spacing: -1px;
`;

const UserLocation = styled.div`
  color: var(--Gray-02, #ccc);
  font-family: Pretendard;
  font-size: 20px;
  font-style: normal;
  font-weight: 600;
  line-height: 140%; /* 28px */
  letter-spacing: -1px;
`;

const ContentsBox = styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 187px;
`;

const PostBox = styled.div`
  display: flex;
  text-align: center;
  width: 426px;
  height: 406px;
  border: 10px solid rgba(black, 0.1);
  flex-shrink: 0;
  border-radius: 16px;
  background: var(--White, #fff);
  /* Shadow 01 */
  box-shadow: 0px 4px 20px 0px rgba(51, 51, 51, 0.1);
`;

const PostContents = styled.div``;

const ReviewBox = styled.div`
  width: 426px;
  height: 406px;
  border-radius: 16px;
  background: var(--White, #fff);
  /* Shadow 01 */
  box-shadow: 0px 4px 20px 0px rgba(51, 51, 51, 0.1);
  div {
    color: var(--Black, #222);
    font-family: Pretendard;
    font-size: 32px;
    font-style: normal;
    font-weight: 600;
    line-height: 140%; /* 44.8px */
    letter-spacing: -1px;
  }
  p {
    color: var(--Black, #222);
    font-family: Pretendard;
    font-size: 16px;
    font-style: normal;
    font-weight: 500;
    line-height: 140%; /* 22.4px */
    letter-spacing: -1px;
  }
`;
